INSERT INTO `9b55fB_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(1, 1, 0, 'tpl.AjaxForm.example', '', 0, 4, 0, '<form action="" method="post" class="ajax_form af_example">\n\n    <div class="form-group">\n        <label class="control-label" for="af_name">[[%af_label_name]]</label>\n        <div class="controls">\n            <input type="text" id="af_name" name="name" value="[[+fi.name]]" placeholder="" class="form-control"/>\n            <span class="error_name">[[+fi.error.name]]</span>\n        </div>\n    </div>\n\n    <div class="form-group">\n        <label class="control-label" for="af_email">[[%af_label_email]]</label>\n        <div class="controls">\n            <input type="email" id="af_email" name="email" value="[[+fi.email]]" placeholder="" class="form-control"/>\n            <span class="error_email">[[+fi.error.email]]</span>\n        </div>\n    </div>\n\n    <div class="form-group">\n        <label class="control-label" for="af_message">[[%af_label_message]]</label>\n        <div class="controls">\n            <textarea id="af_message" name="message" class="form-control" rows="5">[[+fi.message]]</textarea>\n            <span class="error_message">[[+fi.error.message]]</span>\n        </div>\n    </div>\n\n    <div class="form-group">\n        <div class="controls">\n            <button type="reset" class="btn btn-default">[[%af_reset]]</button>\n            <button type="submit" class="btn btn-primary">[[%af_submit]]</button>\n        </div>\n    </div>\n\n    [[+fi.success:is=`1`:then=`\n    <div class="alert alert-success">[[+fi.successMessage]]</div>\n    `]]\n    [[+fi.validation_error:is=`1`:then=`\n    <div class="alert alert-danger">[[+fi.validation_error_message]]</div>\n    `]]\n</form>', 0, NULL, 0, 'core/components/ajaxform/elements/chunks/chunk.example.tpl'),
(2, 0, 0, 'lgnLoginTpl', '', 0, 5, 0, '<div class="loginForm">\n    <div class="loginMessage">[[+errors]]</div>\n    <div class="loginLogin">\n        <form class="loginLoginForm" action="[[~[[*id]]]]" method="post">\n            <fieldset class="loginLoginFieldset">\n                <legend class="loginLegend">[[+actionMsg]]</legend>\n                <label class="loginUsernameLabel">[[%login.username]]\n                    <input class="loginUsername" type="text" name="username" />\n                </label>\n                \n                <label class="loginPasswordLabel">[[%login.password]]\n                    <input class="loginPassword" type="password" name="password" />\n                </label>\n                <input class="returnUrl" type="hidden" name="returnUrl" value="[[+request_uri]]" />\n\n                [[+login.recaptcha_html]]\n                \n                <input class="loginLoginValue" type="hidden" name="service" value="login" />\n                <span class="loginLoginButton"><input type="submit" name="Login" value="[[+actionMsg]]" /></span>\n            </fieldset>\n        </form>\n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(3, 0, 0, 'lgnLogoutTpl', '', 0, 5, 0, '<div class="loginMessage">[[+errors]]</div>\n<br />\n<div class="loginLogin">\n    <div class="loginRegister">\n        <a href="[[+logoutUrl]]" title="[[+actionMsg]]">[[+actionMsg]]</a>\n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(4, 0, 0, 'lgnErrTpl', '', 0, 5, 0, '<p class="error">[[+msg]]</p>', 0, 'a:0:{}', 0, ''),
(5, 0, 0, 'lgnForgotPassEmail', '', 0, 5, 0, '[[%login.forgot_password_email_text? &username=`[[+username]]` &confirmUrl=`[[+confirmUrl]]` &password=`[[+password]]`]]\n', 0, 'a:0:{}', 0, ''),
(6, 0, 0, 'lgnForgotPassSentTpl', '', 0, 5, 0, '<h2>Your Login Information Has Been Sent</h2>\n\n<p>Your login information has been sent to the email address [[+email]].</p>', 0, 'a:0:{}', 0, ''),
(7, 0, 0, 'lgnForgotPassTpl', '', 0, 5, 0, '<div class="loginFPErrors">[[+loginfp.errors]]</div>\n<div class="loginFP">\n    <form class="loginFPForm" action="[[~[[*id]]]]" method="post">\n        <fieldset class="loginFPFieldset">\n            <legend class="loginFPLegend">[[%login.forgot_password]]</legend>\n            <label class="loginFPUsernameLabel">[[%login.username]]\n                <input class="loginFPUsername" type="text" name="username" value="[[+loginfp.post.username]]" />\n            </label>\n            \n            <p>[[%login.or_forgot_username]]</p>\n            \n            <label class="loginFPEmailLabel">[[%login.email]]\n                <input class="loginFPEmail" type="text" name="email" value="[[+loginfp.post.email]]" />\n            </label>\n            \n            <input class="returnUrl" type="hidden" name="returnUrl" value="[[+loginfp.request_uri]]" />\n            \n            <input class="loginFPService" type="hidden" name="login_fp_service" value="forgotpassword" />\n            <span class="loginFPButton"><input type="submit" name="login_fp" value="[[%login.reset_password]]" /></span>\n        </fieldset>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(8, 0, 0, 'lgnResetPassTpl', '', 0, 5, 0, '<div class="loginResetPass">\n<p class="loginResetPassHeader">[[+username]],</p>\n\n<p class="loginResetPassText">Your password has been reset. Please return <a href="[[+loginUrl]]">here</a> to log in.</p>  \n</div>', 0, 'a:0:{}', 0, ''),
(9, 0, 0, 'lgnRegisterFormTpl', '', 0, 5, 0, '<div class="register">\n    <div class="registerMessage">[[+error.message]]</div>\n    \n    <form class="form" action="[[~[[*id]]]]" method="post">\n        <input type="hidden" name="nospam:blank" value="" />\n        \n        <label for="username">[[%register.username? &namespace=`login` &topic=`register`]]\n            <span class="error">[[+error.username]]</span>\n        </label>\n        <input type="text" name="username:required:minLength=6" id="username" value="[[+username]]" />\n        \n        <label for="password">[[%register.password]]\n            <span class="error">[[+error.password]]</span>\n        </label>\n        <input type="password" name="password:required:minLength=6" id="password" value="[[+password]]" />\n        \n        <label for="password_confirm">[[%register.password_confirm]]\n            <span class="error">[[+error.password_confirm]]</span>\n        </label>\n        <input type="password" name="password_confirm:password_confirm=`password`" id="password_confirm" value="[[+password_confirm]]" />\n        \n        <label for="fullname">[[%register.fullname]]\n            <span class="error">[[+error.fullname]]</span>\n        </label>\n        <input type="text" name="fullname:required" id="fullname" value="[[+fullname]]" />\n        \n        <label for="email">[[%register.email]]\n            <span class="error">[[+error.email]]</span>\n        </label>\n        <input type="text" name="email:email" id="email" value="[[+email]]" />\n        \n        <br class="clear" />\n\n        [[+register.recaptcha_html]]\n        [[+error.recaptcha]]\n        \n        <div class="form-buttons">\n            <input type="submit" name="login-register-btn" value="Register" />\n        </div>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(10, 0, 0, 'lgnActivateEmailTpl', '', 0, 5, 0, '<p>[[+username]],</p>\n\n<p>Thanks for registering! To activate your new account, please click on the following link:</p>\n\n<p><a href="[[+confirmUrl]]">[[+confirmUrl]]</a></p>\n\n<p>After activating, you may login with your password and username:</p>\n\n<p>\nUsername: <strong>[[+username]]</strong><br />\nPassword: <strong>[[+password]]</strong></p>\n\n<p>If you did not request this message, please ignore it.</p>\n\n<p>Thanks,<br />\n<em>Site Administrator</em></p>', 0, 'a:0:{}', 0, ''),
(11, 0, 0, 'lgnActiveUser', '', 0, 5, 0, '<li>[[+username]]</li>', 0, 'a:0:{}', 0, ''),
(12, 0, 0, 'lgnChangePassword', '', 0, 5, 0, '[[!+logcp.successMessage:notempty=`<p style="color: red;">[[+logcp.successMessage]]</p>`]]\n\n<form class="form inline" action="[[~[[*id]]]]" method="post">\n    <input type="hidden" name="nospam:blank" value="" />\n\n    <div class="ff">\n        <label for="password_old">[[!%login.password_old? &namespace=`login` &topic=`changepassword`]]\n            <span class="error">[[+logcp.error.password_old]]</span>\n        </label>\n        <input type="password" name="password_old:required" id="password_old" value="[[+logcp.password_old]]" />\n    </div>\n\n    <div class="ff">\n        <label for="password_new">[[!%login.password_new]]\n            <span class="error">[[+logcp.error.password_new]]</span>\n        </label>\n        <input type="password" name="password_new:required" id="password_new" value="[[+logcp.password_new]]" />\n    </div>\n\n    <div class="ff">\n        <label for="password_new_confirm">[[!%login.password_new_confirm]]\n            <span class="error">[[+logcp.error.password_new_confirm]]</span>\n        </label>\n        <input type="password" name="password_new_confirm:required" id="password_new_confirm" value="[[+logcp.password_new_confirm]]" />\n    </div>\n\n    <br class="clear" />\n\n    <div class="form-buttons">\n        <input type="submit" name="logcp-submit" value="[[!%login.change_password]]" />\n    </div>\n</form>', 0, 'a:0:{}', 0, ''),
(13, 0, 0, 'lgnResetPassChangePassTpl', '', 0, 5, 0, '[[!+logcp.error_message:notempty=`<p style="color: red;">[[+logcp.error_message]]</p>`]]\n\n<form class="form inline" action="" method="post">\n    <input type="hidden" name="nospam:blank" value="" />\n    <input type="hidden" name="lp" value="[[!+logcp.lp]]"/>\n    <input type="hidden" name="lu" value="[[!+logcp.lu]]"/>\n\n    <div class="ff">\n        <label for="password_new">[[!%login.password_new]]\n            <span class="error">[[+logcp.error.password_new]]</span>\n        </label>\n        <input type="password" name="password_new:required" id="password_new" value="[[+logcp.password_new]]" />\n    </div>\n\n    <div class="ff">\n        <label for="password_new_confirm">[[!%login.password_new_confirm]]\n            <span class="error">[[+logcp.error.password_new_confirm]]</span>\n        </label>\n        <input type="password" name="password_new_confirm:required" id="password_new_confirm" value="[[+logcp.password_new_confirm]]" />\n    </div>\n\n    <br class="clear" />\n\n    <div class="form-buttons">\n        <input type="submit" name="logcp-submit" value="[[!%login.change_password]]" />\n    </div>\n</form>', 0, 'a:0:{}', 0, ''),
(14, 0, 0, 'lgnExpiredTpl', '', 0, 5, 0, '<p><strong>Password Reset Information</strong></p>\n<p>Your password has already been reset or the link expired. If you need to reset your password again, click <a href="#">here</a>.</p>', 0, 'a:0:{}', 0, ''),
(15, 0, 0, 'lgnUpdateProfile', '', 0, 5, 0, '<div class="update-profile">\n    <div class="updprof-error">[[+error.message]]</div>\n    [[+login.update_success:if=`[[+login.update_success]]`:is=`1`:then=`[[%login.profile_updated? &namespace=`login` &topic=`updateprofile`]]`]]\n\n    <form class="form" action="[[~[[*id]]]]" method="post">\n        <input type="hidden" name="nospam:blank" value="" />\n\n        <label for="fullname">[[!%login.fullname? &namespace=`login` &topic=`updateprofile`]]\n            <span class="error">[[+error.fullname]]</span>\n        </label>\n        <input type="text" name="fullname" id="fullname" value="[[+fullname]]" />\n\n        <label for="email">[[!%login.email]]\n            <span class="error">[[+error.email]]</span>\n        </label>\n        <input type="text" name="email:required:email" id="email" value="[[+email]]" />\n\n        <label for="phone">[[!%login.phone]]\n            <span class="error">[[+error.phone]]</span>\n        </label>\n        <input type="text" name="phone" id="phone" value="[[+phone]]" />\n\n        <label for="mobilephone">[[!%login.mobilephone]]\n            <span class="error">[[+error.mobilephone]]</span>\n        </label>\n        <input type="text" name="mobilephone" id="mobilephone" value="[[+mobilephone]]" />\n\n        <label for="fax">[[!%login.fax]]\n            <span class="error">[[+error.fax]]</span>\n        </label>\n        <input type="text" name="fax" id="fax" value="[[+fax]]" />\n\n        <label for="address">[[!%login.address]]\n            <span class="error">[[+error.address]]</span>\n        </label>\n        <input type="text" name="address" id="address" value="[[+address]]" />\n\n        <label for="country">[[!%login.country]]\n            <span class="error">[[+error.country]]</span>\n        </label>\n        <input type="text" name="country" id="country" value="[[+country]]" />\n\n        <label for="city">[[!%login.city]]\n            <span class="error">[[+error.city]]</span>\n        </label>\n        <input type="text" name="city" id="city" value="[[+city]]" />\n\n        <label for="state">[[!%login.state]]\n            <span class="error">[[+error.state]]</span>\n        </label>\n        <input type="text" name="state" id="state" value="[[+state]]" />\n\n        <label for="zip">[[!%login.zip]]\n            <span class="error">[[+error.zip]]</span>\n        </label>\n        <input type="text" name="zip" id="zip" value="[[+zip]]" />\n\n        <label for="website">[[!%login.website]]\n            <span class="error">[[+error.website]]</span>\n        </label>\n        <input type="text" name="website" id="website" value="[[+website]]" />\n\n        <br class="clear" />\n\n        <div class="form-buttons">\n            <input type="submit" name="login-updprof-btn" value="[[!%login.update_profile]]" />\n        </div>\n    </form>\n</div>', 0, 'a:0:{}', 0, ''),
(16, 1, 0, 'tpl.HybridAuth.login', 'Chunk for guest', 0, 6, 0, '[[%ha.login_intro]]\n<br/>\n[[+providers]]\n\n[[+error:notempty=`\n<div class="alert alert-block alert-error">[[+error]]</div>`]]', 0, NULL, 0, 'core/components/hybridauth/elements/chunks/chunk.login.tpl'),
(17, 1, 0, 'tpl.HybridAuth.logout', 'Chunk for logged in', 0, 6, 0, '<div class="hybridauth">\n    <img src="[[+gravatar]]?s=75" alt="[[+username]]" title="[[+fullname]]" class="ha-avatar"/>\n\n    <span class="ha-info">\n        [[%ha.greeting]] <b>[[+username]]</b> ([[+fullname]])! <a href="[[+logout_url]]">[[%ha.logout]]</a>\n        <br/><br/>\n        <small>[[%ha.providers_available]]</small><br/>\n        [[+providers]]\n    </span>\n\n</div>\n\n[[+error:notempty=`\n<div class="alert alert-block alert-error">[[+error]]</div>`]]\n', 0, NULL, 0, 'core/components/hybridauth/elements/chunks/chunk.logout.tpl'),
(18, 1, 0, 'tpl.HybridAuth.profile', 'Chunk for profile update', 0, 6, 0, '<form action="[[~[[*id]]]]" method="post" class="form-horizontal">\n\n    <div class="control-group">\n        <label class="control-label">[[%ha.gravatar]]</label>\n        <div class="controls">\n            <img src="[[+gravatar]]?s=100" alt="[[+email]]" title="[[+email]]" style="margin-left:40px;"/>\n            <br/>\n            <small>[[%ha.gravatar_desc]]</small>\n        </div>\n    </div>\n\n    <div class="control-group[[+error.username:notempty=` error`]]">\n        <label class="control-label">[[%ha.username]]</label>\n        <div class="controls">\n            <input type="text" name="username" value="[[+username]]"/>\n            <span class="help-inline">[[+error.username]]</span>\n        </div>\n    </div>\n\n    <div class="control-group[[+error.fullname:notempty=` error`]]">\n        <label class="control-label">[[%ha.fullname]]</label>\n        <div class="controls">\n            <input type="text" name="fullname" value="[[+fullname]]"/>\n            <span class="help-inline">[[+error.fullname]]</span>\n        </div>\n    </div>\n\n    <div class="control-group[[+error.email:notempty=` error`]]">\n        <label class="control-label">[[%ha.email]]</label>\n        <div class="controls">\n            <input type="text" name="email" value="[[+email]]"/>\n            <span class="help-inline">[[+error.email]]</span>\n        </div>\n    </div>\n\n    <div class="control-group">\n        <label class="control-label">[[%ha.providers_available]]</label>\n        <div class="controls">\n            [[+providers]]\n        </div>\n    </div>\n\n    <input type="hidden" name="hauth_action" value="updateProfile"/>\n    <div class="form-actions">\n        <button type="submit" class="btn btn-primary">[[%ha.save_profile]]</button>\n        &nbsp;&nbsp;\n        <a href="[[+logout_url]]" class="btn btn-danger">[[%ha.logout]]</a>\n    </div>\n</form>\n[[+success:is=`1`:then=`\n<div c