(22, 1, 0, 'Navbar', 'Navbar with main navigation', 0, 7, 0, '<!-- Static navbar -->\r\n<div class="navbar navbar-default navbar-static-top" role="navigation">\r\n	<div class="container">\r\n		<div class="navbar-header">\r\n			<button type="button" class="navbar-toggle" data-toggle="collapse" data-target=".navbar-collapse">\r\n				<span class="sr-only">Toggle navigation</span>\r\n				<span class="icon-bar"></span>\r\n				<span class="icon-bar"></span>\r\n				<span class="icon-bar"></span>\r\n			</button>\r\n			<a class="navbar-brand" href="/">[[++site_name]]</a>\r\n		</div>\r\n		<div class="navbar-collapse collapse">\r\n			<ul class="nav navbar-nav">\r\n				[[pdoMenu?\r\n					&startId=`0`\r\n					&level=`2`\r\n					&tplParentRow=`@INLINE\r\n					<li class="[[+classnames]] dropdown">\r\n						<a href="#" class="dropdown-toggle" data-toggle="dropdown" [[+attributes]]>[[+menutitle]]<b class="caret"></b></a>\r\n						<ul class="dropdown-menu">[[+wrapper]]</ul>\r\n					</li>`\r\n					&tplOuter=`@INLINE [[+wrapper]]`\r\n				]]\r\n			</ul>\r\n		</div><!--/.nav-collapse -->\r\n	</div>\r\n</div>', 0, NULL, 0, 'assets/components/themebootstrap/elements/chunks/chunk.navbar.tpl'),
